import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar({ onThemeChange }) {
  const [theme, setTheme] = useState('frog'); // ÈªòËÆ§‰∏ªÈ¢ò‰∏∫ "frog"

  const handleThemeSwitch = () => {
    // ÂàáÊç¢‰∏ªÈ¢òÈÄªËæë
    const nextTheme = theme === 'frog' ? 'christmas' : theme === 'christmas' ? 'halloween' : 'frog';
    setTheme(nextTheme);
    onThemeChange(nextTheme); // ÈÄöÁü•Áà∂ÁªÑ‰ª∂‰∏ªÈ¢òÂ∑≤Êõ¥Êîπ
  };

  const getIcon = () => {
    // Ê†πÊçÆ‰∏ªÈ¢òÊòæÁ§∫ÂØπÂ∫îÁöÑÂõæÊ†á
    if (theme === 'frog') return 'üê∏';
    if (theme === 'christmas') return 'üéÑ';
    if (theme === 'halloween') return 'üòà';
  };

  return (
    <nav>
      {/* Ê†áÈ¢ò */}
      <h1>Ricky Rain Frog</h1>

      {/* ‰∏≠Èó¥ÁöÑÂØºËà™ÈìæÊé• */}
      <div className="nav-links">
        <Link to="/">Home</Link>
        <Link to="/dialog">Dialog</Link>
        <Link to="/cartoon">Cartoon</Link>
      </div>

      {/* Âä®ÊÄÅ‰∏ªÈ¢òÂàáÊç¢ÊåâÈíÆ */}
      <button className="theme-button" onClick={handleThemeSwitch}>
        {getIcon()}
      </button>

      {/* Take Ricky Home ÊåâÈíÆ */}
      <Link to="/shop" className="shop-button">
        Take Ricky Home
      </Link>
    </nav>
  );
}

export default Navbar;
